.navigation{
    /* position: fixed; */
    /* top: 20px;
    left: 20px;
    bottom: 20px; */
    height: 781px;
    width: 295px;
    box-sizing: initial;
    border-left: 5px solid #301759;
    background-color: #301759;
    /* transition: width 0.5s; */
}

.navigation ul{
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    padding-left: 5px;
    padding-top: 40px;
}

.navigation ul li{
    position: relative;
    list-style: none;
    width: 100%;
    border-top-left-radius: 20px;
    border-bottom-left-radius: 20px;
}

.navigation ul li b:nth-child(1){
    position: absolute;
    top: -20px;
    height: 20px;
    width: 100%;
    background: #DCE8FF;
    display: none;
}

.navigation ul li b:nth-child(1)::before{
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border-bottom-right-radius: 20px;
    background: #301759;
}

.navigation ul li b:nth-child(2){
    position: absolute;
    bottom: -20px;
    height: 20px;
    width: 100%;
    background: #DCE8FF;
    display: none;
}

.navigation ul li b:nth-child(2)::before{
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border-top-right-radius: 20px;
    background: #301759;
}

.navigation ul li.active b:nth-child(1),
.navigation ul li.active b:nth-child(2){
    display: block;
}

.navigation ul li.active{
    background-color: #DCE8FF
}

.navigation ul li a{
    position: relative;
    display: block;
    width: 100%;
    display: flex;
    text-decoration: none;
    color: #fff;
}

.navigation ul li.active a{
    color: #000;
}

.navigation ul li a .title{
    position: relative;
    display: block;
    padding-left: 80px;
    height: 60px;
    line-height: 60px;
    white-space: normal;
    font-size: 1.5rem;
}



.table{
    margin: 4rem 7rem;
    font-size: 20px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);   
}
/* CSS for alternating row colors */
.table tbody tr:nth-child(even) {
    background-color: #E8E8E8;
  }

.table tbody tr:nth-child(odd) {
    background-color: #CAFFF5;
  }

  /* CSS for the first row */
.table tbody tr:first-child {
    background-color: #9C9AFE;
    font-size: 24px;
  }

  /* CSS for table cell height */
.table td,
.table th {
  height: 60px; 
  width: 220px;
  /* border: 1px solid black; */
  padding-top: 5px;
  text-align: center;
}



/* Target rows starting from the second row */
.table tr:nth-child(n+2) td:nth-child(5) {
    display: flex;
    justify-content: space-between;
    padding: 17px 50px;
}


.table tbody tr td h1 {
    color: blue;
}

.table tbody tr td h2 {
    color: red;
}
